mapper.select:
SELECT cue.user_id as system_id, cue.user_name as system_name, count(cbm.sys_user_id) AS system_count FROM cl_user_examine cue LEFT JOIN cl_borrow_main cbm ON cue.user_id = cbm.sys_user_id
AND cbm.state in
#{item}
and cbm.create_time >= #{startTime,jdbcType=TIMESTAMP}
and cbm.create_time <= #{endTime,jdbcType=TIMESTAMP}
WHERE cue.`status` = '1' GROUP BY system_id ORDER BY system_count LIMIT 1

SELECT m.* FROM cl_borrow_main m WHERE EXISTS (
SELECT * FROM cl_borrow t WHERE t.id=#{borrowId} AND t.borrow_main_id=m.id AND m.user_id=t.user_id
) AND m.user_id=#{userId}
