select u.id, u.name, u.user_name as userName from arc_sys_user u INNER JOIN arc_sys_user_role ur on u.id = ur.user_id INNER JOIN arc_sys_role r on r.id = ur.role_id where r.nid = #{roleName}
and u.office_id in
#{item}

select u.id, u.name as name, u.office_id as officeId from arc_sys_user u,arc_sys_user_role ur,arc_sys_role r where u.id=ur.user_id and r.id=ur.role_id
and u.office_id like concat('%',#{officeId}, '%')
and r.nid = #{nid}
and u.id in
#{item}

select u.id as id, u.office_over as officeOver, u.email as email, u.phone as phone, u.mobile as mobile, u.remark as remark, u.position as position, u.name as name, u.user_name as userName, u.job_num as job_num, u.office_id as officeId, u.status as status, group_concat( u4.id) as roleId, group_concat( u4.name) as roleName from arc_sys_user u, arc_sys_user_role u2, arc_sys_role u4 where u.id = u2.user_id and u2.role_id = u4.id
u.id = #{id}
and u.name like concat('%',#{name}, '%')
and u.job_num like concat('%',#{number}, '%')
and u.email = #{email}
and u.phone = #{phone}
and u.mobile = #{mobile}
and u.status = #{status}
and u.position = #{position}
and u2.role_id = #{roleId}
group by u.id,u.email,u.phone,u.mobile,u.remark,u.position
